# Load required libraries
library(shiny)
library(DT)
library(dplyr)

# Define UI
ui <- fluidPage(
  titlePanel("JHACH Fellow Seminar and Happy Hour Schedule"),
  
  # Create a tabset panel for multiple tabs
  tabsetPanel(
    # Tab 1: Seminar Schedule
    tabPanel(
      "Seminar Schedule",
      sidebarLayout(
        sidebarPanel(
          checkboxInput("filter_lab", "Filter by Lab", FALSE),
          uiOutput("lab_filter_ui"),
          checkboxInput("filter_host", "Filter by Host", FALSE),
          uiOutput("host_filter_ui")
        ),
        mainPanel(
          DTOutput("seminar_table")
        )
      )
    ),
    
    # Tab 2: Happy Hour Schedule
    tabPanel(
      "Happy Hour Schedule",
      sidebarLayout(
        sidebarPanel(
          checkboxInput("filter_location", "Filter by Location", FALSE),
          uiOutput("location_filter_ui")
        ),
        mainPanel(
          DTOutput("happyhour_table")
        )
      )
    )
  )
)

# Define server logic
server <- function(input, output, session) {
  
  # Read the seminar schedule CSV file directly from the server
  seminar_data <- reactive({
    read.csv("seminar_schedule.csv")
  })
  
  # Read the happy hour schedule CSV file directly from the server
  happyhour_data <- reactive({
    read.csv("happyhour_schedule.csv")
  })
  
  # Render UI for lab filter (Seminar Schedule)
  output$lab_filter_ui <- renderUI({
    if (input$filter_lab) {
      selectInput("selected_lab", "Select Lab", choices = unique(seminar_data()$Lab))
    }
  })
  
  # Render UI for host filter (Seminar Schedule)
  output$host_filter_ui <- renderUI({
    if (input$filter_host) {
      selectInput("selected_host", "Select Host", choices = unique(seminar_data()$Host))
    }
  })
  
  # Render UI for location filter (Happy Hour Schedule)
  output$location_filter_ui <- renderUI({
    if (input$filter_location) {
      selectInput("selected_location", "Select Location", choices = unique(happyhour_data()$Location))
    }
  })
  
  # Filter seminar data based on selected lab and host
  filtered_seminar_data <- reactive({
    df <- seminar_data()
    if (input$filter_lab && !is.null(input$selected_lab)) {
      df <- df[df$Lab == input$selected_lab, ]
    }
    if (input$filter_host && !is.null(input$selected_host)) {
      df <- df[df$Host == input$selected_host, ]
    }
    df
  })
  
  # Filter happy hour data based on selected location
  filtered_happyhour_data <- reactive({
    df <- happyhour_data()
    if (input$filter_location && !is.null(input$selected_location)) {
      df <- df[df$Location == input$selected_location, ]
    }
    df
  })
  
  # Render the seminar schedule table
  output$seminar_table <- renderDT({
    datatable(filtered_seminar_data(), options = list(pageLength = 10, autoWidth = TRUE))
  })
  
  # Render the happy hour schedule table
  output$happyhour_table <- renderDT({
    datatable(filtered_happyhour_data(), options = list(pageLength = 10, autoWidth = TRUE))
  })
}

# Run the app
shinyApp(ui = ui, server = server)
